import * as mongoose from 'mongoose';
import { ContactSchema } from '../db/models/crmModel';
import { Request, Response } from 'express';

const Contact = mongoose.model('Contact', ContactSchema);

export class ContactController {

    // Add/Save new Data
    public addNewContact(req: Request, res: Response) {
        let newConatct = new Contact(req.body);
        newConatct.save((err, contact) => {
            if (err) {
                res.send(err);
            }
            else {
                res.json(contact);
            }
        })
    }

    public async getAllContacts(req: Request, res: Response) {

        let allContacts = await Contact.find({});
        console.log("Hello")
        res.send(allContacts);
    }

    //Get Single Contact Details
    public getContactWithID(req: Request, res: Response) {
        Contact.findById(req.params.contactId, (err, contact) => {
            if (err) {
                res.send(err);
            }
            res.json(contact);
        });
    }

    public async addAddress(req: Request, res: Response) {
        console.log(req.params.contactId);
        console.log(req.body);

        try{
            let addressAdd = await Contact.update(
                { _id: req.params.contactId },
                { $addToSet : { address:[req.body]}}
            );
        res.send(addressAdd)
        }catch(err){
            res.send(err)
        }
    }
}

//   /lib/models/crmModel.ts

import * as mongoose from 'mongoose';
const Schema = mongoose.Schema;


export var addressSchema = new Schema({
    addressId :{
        type: Number,
        index: true,
        unique: true
    },
    city:{ type: String },
    state:{ type: String },
    _id: false 
});

export const ContactSchema = new Schema({
    firstName: {
        type: String,
        required: 'Enter a first name'
    },
    lastName: {
        type: String,
        required: 'Enter a last name'
    },
    email: {
        type: String,
        trim: true,
        index: true,
        unique: true,
        required: true,
    },
    company: { type: String },
    phone: { type: Number },
    created_date: {
        type: Date,
        default: Date.now
    },
    address: [addressSchema]
})

const { check, validationResult } = require('express-validator');

export const AddSchema = [
    check('firstName')
        .not()
        .isEmpty()
        .withMessage('first name can not be empty!')
        .bail()
        .isLength({ min: 3 })
        .withMessage('Minimum 3 characters required!')
        .bail()
    , (req, res, next) => {
        const errors = validationResult(req);
        if (!errors.isEmpty())
            return res.status(422).json({ errors: errors.array() });
        next();
    },
]

import mongoose from "mongoose";
const DB_URI = 'mongodb+srv://admin:admin@cluster0.xfyco.mongodb.net/dev';

export function connect(){
    return new Promise((resolve, reject) => {
    // mongoose.Promise = global.Promise;

    if (process.env.NODE_ENV === 'test') {
        console.log("Mock DB Connected");
        const Mockgoose = require('mockgoose').Mockgoose;
        const mockgoose = new Mockgoose(mongoose);
  
        mockgoose.prepareStorage()
          .then(() => {
            mongoose.connect(DB_URI,
              { useNewUrlParser: true, useCreateIndex: true })
              .then((res, err) => {
                if (err) return reject(err);
                // console.log("Test DB Connected");
                resolve();
              })
          })
    }else /* istanbul ignore next */ {
        console.log("Dev DB Connected");
        mongoose.connect(DB_URI, {
            useCreateIndex: true,
            useNewUrlParser: true,
            useUnifiedTopology: true
        })
        .then((res, err) => {
            if (err) return reject(err);
            resolve();
          })
    }
})
}

export function close(){
    return mongoose.disconnect();
}
